头文件定义插入键值并返回插入位置叶结点插入叶结点分裂内部结点插入（含分裂） <pre><code>#ifndef _BP_TREE_H_
#define _BP_TREE_H_
#define MAX 3
#define MAXN 10005
typedef struct node *Tree;
typedef struct node
{
    int size;
    int isLeaf;
    int key[MAX + 1];
    Tree parent;
    Tree child[MAX + 1];
} Node;
int InsertValue(Tree p, int key);
Tree InsertLeaf(Tree root, int key);
Tree SplitLeaf(Tree p, Tree parent, Tree root, int key);
Tree InsertInternal(Tree p, Tree Lefttree, Tree Righttree, Tree root, int key);
void PrintTree(Tree root);
#endif
</code></pre> <pre><code>int InsertValue(Tree p, int key) {
    int i = 0;
    while (i &lt; p-&gt;size &amp;&amp; key &gt; p-&gt;key[i]) {
        i++;
    }
    if (key == p-&gt;key[i]) {
        printf("Key %d is duplicated\n", key);
        return -1;
    };
    for (int j = p-&gt;size; j &gt; i; j--) {
        p-&gt;key[j] = p-&gt;key[j - 1];
    }
    p-&gt;key[i] = key;
    p-&gt;size++;
    return i;
}
</code></pre> <pre><code>Tree InsertLeaf(Tree root, int key) {
    if (!root) {
        root = (Tree)malloc(sizeof(Node));
        root-&gt;size = 1;
        root-&gt;isLeaf = 1;
        root-&gt;key[0] = key;
        root-&gt;parent = NULL;
        for (int i = 0; i &lt; MAX; i++) {
            root-&gt;child[i] = NULL;
        }
    } else {
        Tree p = root;
        Tree parent = NULL;
        while (!p-&gt;isLeaf) {
            parent = p;
            for (int i = 0; i &lt; p-&gt;size; i++) {
                if (key &lt; p-&gt;key[i]) {
                    p = p-&gt;child[i];
                    break;
                }
                if (i == p-&gt;size - 1) {
                    p = p-&gt;child[i + 1];
                    break;
                }
            }
        }
        if (p-&gt;size &lt; MAX) {
            if (InsertValue(p, key) == -1) {
                return root;
            }
            p-&gt;parent = parent;
        } else {
            root = SplitLeaf(p, parent, root, key);
        }
    }
    return root;
}
</code></pre> <pre><code>Tree SplitLeaf(Tree p, Tree parent, Tree root, int key) {
    Tree LeftTree = (Tree)malloc(sizeof(Node));
    Tree RightTree = (Tree)malloc(sizeof(Node));
    if (InsertValue(p, key) == -1) {
        return root;
    }
    LeftTree-&gt;size = (MAX + 1) / 2;
    RightTree-&gt;size = MAX + 1 - (MAX + 1) / 2;
    LeftTree-&gt;isLeaf = RightTree-&gt;isLeaf = 1;
    for (int i = 0; i &lt; LeftTree-&gt;size; i++) {
        LeftTree-&gt;key[i] = p-&gt;key[i];
    }
    for (int i = 0; i &lt; RightTree-&gt;size; i++) {
        RightTree-&gt;key[i] = p-&gt;key[i + LeftTree-&gt;size];
    }
    if (p == root) {
        Tree newRoot = (Tree)malloc(sizeof(Node));
        newRoot-&gt;size = 1;
        newRoot-&gt;key[0] = RightTree-&gt;key[0];
        newRoot-&gt;isLeaf = 0;
        newRoot-&gt;child[0] = LeftTree;
        newRoot-&gt;child[1] = RightTree;
        root = newRoot;
        LeftTree-&gt;parent = RightTree-&gt;parent = newRoot;
    } else {
        root = InsertInternal(parent, LeftTree, RightTree, root, RightTree-&gt;key[0]);
    }
    return root;
}
</code></pre> <pre><code>Tree InsertInternal(Tree p, Tree LeftTree, Tree RightTree, Tree root, int key) {
    if (p-&gt;size &lt; MAX - 1) { // insert directly
        int i = InsertValue(p, key);
        if (i == -1) {
            return root;
        }
        for (int j = p-&gt;size; j &gt; i + 1; j--) {
            p-&gt;child[j] = p-&gt;child[j - 1];
        }
        p-&gt;child[i] = LeftTree;
        p-&gt;child[i + 1] = RightTree;
        LeftTree-&gt;parent = RightTree-&gt;parent = p;
    } else { // split internal
        Tree LeftChild = (Tree)malloc(sizeof(Node));
        Tree RightChild = (Tree)malloc(sizeof(Node));
        Tree ptrarray[MAX + 1];
        for(int i = 0; i &lt; MAX + 1; i++) {
            ptrarray[i] = NULL;
        }
        for(int i = 0; i &lt; MAX + 1; i++) {
            ptrarray[i] = p-&gt;child[i];
        }
        int i = InsertValue(p, key);
        if (i == -1) {
            return root;
        }
        for (int j = MAX + 1; j &gt; i + 1; j--) {
            ptrarray[j] = ptrarray[j - 1];
        }
        ptrarray[i] = LeftTree;
        ptrarray[i + 1] = RightTree;
        LeftChild-&gt;isLeaf = RightChild-&gt;isLeaf = 0;
        // the size is number of children - 1
        LeftChild-&gt;size = (MAX + 1) / 2 - 1;
        RightChild-&gt;size = (MAX + 1) - (MAX + 1) / 2 - 1;
        // note that the most left branch of right child dose not need a key
        for (int i = 0; i &lt; LeftChild-&gt;size; i++) {
            LeftChild-&gt;key[i] = p-&gt;key[i];
        }
        for (int i = 0; i &lt; RightChild-&gt;size; i++) {
            RightChild-&gt;key[i] = p-&gt;key[i + LeftChild-&gt;size + 1];
        }
        for (int i = 0; i &lt; LeftChild-&gt;size + 1; i++) {
            LeftChild-&gt;child[i] = ptrarray[i];
            LeftChild-&gt;child[i]-&gt;parent = LeftChild;
        }
        for (int i = 0; i &lt; RightChild-&gt;size + 1; i++) {
            RightChild-&gt;child[i] = ptrarray[i + LeftChild-&gt;size + 1];
            RightChild-&gt;child[i]-&gt;parent = RightChild;
        }
        if (p == root) {
            Tree newRoot = (Tree)malloc(sizeof(Node));
            newRoot-&gt;size = 1;
            newRoot-&gt;key[0] = p-&gt;key[LeftChild-&gt;size];
            newRoot-&gt;isLeaf = 0;
            newRoot-&gt;child[0] = LeftChild;
            newRoot-&gt;child[1] = RightChild;
            root = newRoot;
            LeftChild-&gt;parent = RightChild-&gt;parent = newRoot;
        } else {
            root = InsertInternal(p-&gt;parent, LeftChild, RightChild, root, p-&gt;key[LeftChild-&gt;size]);
        }
    }
    return root;
}
</code></pre>

节点定义递归合并迭代合并 <pre><code>typedef struct node *LeftistHeap;

typedef struct node {
    int key;
    int npl;
    struct node *left;
    struct node *right;
} Node;
</code></pre> <pre><code>LeftistHeap merge_recursive(LeftistHeap h1, LeftistHeap h2) {
    if (h1 == NULL) return h2;
    if (h2 == NULL) return h1;
    // make sure that h1-&gt;key &lt;= h2-&gt;key always holds
    if (h1-&gt;key &gt; h2-&gt;key) {
        LeftistHeap temp = h1;
        h1 = h2;
        h2 = temp;
    }
    h1-&gt;right = merge_recursive(h1-&gt;right, h2);
    /* swap left and right if necessary
    in case that the left child is NULL */
    if (h1-&gt;left == NULL) {
        h1-&gt;left = h1-&gt;right;
        h1-&gt;right = NULL;
    } else {
        if (h1-&gt;left-&gt;npl &lt; h1-&gt;right-&gt;npl) {
            LeftistHeap temp = h1-&gt;left;
            h1-&gt;left = h1-&gt;right;
            h1-&gt;right = temp;
        }
        h1-&gt;npl = h1-&gt;right-&gt;npl + 1;
    }
    return h1;
}
</code></pre> <pre><code>void npl_update(LeftistHeap h) {
    if (h == NULL) return;
    h-&gt;npl = h-&gt;right == NULL ? 0 : h-&gt;right-&gt;npl + 1;
}

LeftistHeap merge_iterative(LeftistHeap h1, LeftistHeap h2) {
    if (h1 == NULL) return h2;
    if (h2 == NULL) return h1;
    LeftistHeap stack[MAX_STACK_SIZE] = {NULL};
    int top = -1;
    LeftistHeap h = NULL;
    LeftistHeap *p = &amp;h;
    while (h1 != NULL &amp;&amp; h2 != NULL) {
        if (h1-&gt;key &gt; h2-&gt;key) {
            LeftistHeap temp = h1;
            h1 = h2;
            h2 = temp;
        }
        stack[++top] = h1;
        *p = h1;
        p = &amp;h1-&gt;right;
        LeftistHeap next = h1-&gt;right;
        h1-&gt;right = h2;
        h1 = next;
    }
    *p = h1 == NULL ? h2 : h1;
    while (top &gt;= 0) {
        npl_update(stack[top--]);
    }
    return h;
}
</code></pre>

