    dfn[v] = low[v] = ++cnt;
    for (ArcNode *arc = G-&gt;vertices[v].firstarc; arc; arc = arc-&gt;nextarc) {
        int w = arc-&gt;adjvex;
        if (!visited[w]) {
            child++;
            FindCut(G, w);
            low[v] = min(low[v], low[w]);
            if (v != 1 &amp;&amp; dfn[v] &lt;= low[w]) {
                printf("%d ", v);
            }
        } else {
            low[v] = min(low[v], dfn[w]);
        }
