adjacency matrixadjacency list <pre><code>#define MaxN 100
int G[MaxN][MaxN];
int pre[MaxN];
int visited[MaxN];
void BFS(int src, int dst) {
int queue[MaxN] = {0};
int front = 0, rear = 0;
queue[rear++] = src;
visited[src] = 1;
while (front &lt; rear) {
int v = queue[front++];
for (int w = 0; w &lt; MaxN; w++) {
if (G[v][w] &amp;&amp; !visited[w]) {
queue[rear++] = w;
visited[w] = 1;
pre[w] = v;
if (w == dst) {
return;
}
}
}
}
}
</code></pre> <pre><code>#define MaxN 100
int visited[MaxN];
int pre[MaxN];
void BFS(ALGraph *G, int src, int dst) {
int queue[MaxN] = {0};
int front = 0, rear = 0;
queue[rear++] = src;
visited[src] = 1;
while (front &lt; rear) {
int v = queue[front++];
for (ArcNode *arc = G-&gt;vertices[v].firstarc; arc; arc = arc-&gt;nextarc) { //访问该节点下的所有邻接点
int w = arc-&gt;adjvex;
if (!visited[w]) {
pre[w] = v;
queue[rear++] = w;
visited[w] = 1;
if (w == dst) {
return;
}
}
}
}
}
